import { useGameContext } from '../context/GameContext';
import { GameState, Skill } from '../types';

export const useSkills = () => {
  const { gameState, updateGameState } = useGameContext();
  
  const startSkill = (skillId: string) => {
    if (!gameState) return;
    
    updateGameState((prevState: GameState | null) => {
      if (!prevState) return prevState;
      
      const newSkills = { ...prevState.player.skills };
      newSkills[skillId] = {
        ...newSkills[skillId],
        isActive: true
      };
      
      return {
        ...prevState,
        player: {
          ...prevState.player,
          skills: newSkills
        }
      };
    });
  };

  const stopSkill = (skillId: string) => {
    if (!gameState) return;
    
    updateGameState((prevState: GameState | null) => {
      if (!prevState) return prevState;
      
      const newSkills = { ...prevState.player.skills };
      newSkills[skillId] = {
        ...newSkills[skillId],
        isActive: false
      };
      
      return {
        ...prevState,
        player: {
          ...prevState.player,
          skills: newSkills
        }
      };
    });
  };
  
  return {
    skills: gameState?.player.skills || {},
    startSkill,
    stopSkill
  };
};
