import React from 'react';
import { useCombat } from '../../hooks/useCombat';
import { ENEMIES } from '../../constants';
import { Enemy } from '../../types';

const CombatPanel: React.FC = () => {
  const { combat, startCombat, stopCombat } = useCombat();
  
  const handleStartCombat = (enemyId: string) => {
    startCombat(enemyId);
  };
  
  const handleStopCombat = () => {
    stopCombat();
  };
  
  return (
    <div className="combat-panel">
      <h2>Combat</h2>
      
      {combat?.isFighting && combat.currentEnemy ? (
        <div className="active-combat">
          <h3>Fighting: {combat.currentEnemy.name}</h3>
          <div className="enemy-stats">
            <p>Health: {combat.currentEnemy.health}/{combat.currentEnemy.maxHealth}</p>
            <div className="health-bar">
              <div 
                className="health" 
                style={{ 
                  width: `${(combat.currentEnemy.health / combat.currentEnemy.maxHealth) * 100}%` 
                }}
              ></div>
            </div>
            <p>Attack: {combat.currentEnemy.attack}</p>
            <p>Defense: {combat.currentEnemy.defense}</p>
          </div>
          <button className="stop-combat-btn" onClick={handleStopCombat}>
            Run Away
          </button>
        </div>
      ) : (
        <div className="enemy-selection">
          <h3>Select an enemy to fight:</h3>
          <div className="enemy-list">
            {Object.keys(ENEMIES).map(enemyId => {
              const enemy = ENEMIES[enemyId] as Enemy;
              return (
                <div key={enemyId} className="enemy-item">
                  <h4>{enemy.name}</h4>
                  <p>Health: {enemy.maxHealth}</p>
                  <p>Attack: {enemy.attack}</p>
                  <p>Defense: {enemy.defense}</p>
                  <button 
                    className="start-combat-btn"
                    onClick={() => handleStartCombat(enemyId)}
                  >
                    Attack
                  </button>
                </div>
              );
            })}
          </div>
        </div>
      )}
    </div>
  );
};

export default CombatPanel;
