import React from 'react';
import { useGameContext } from '../../context/GameContext';
import { Item } from '../../types';

const EquipmentPanel: React.FC = () => {
  const { gameState } = useGameContext();
  const equipment = gameState?.player.equipment || {};
  
  return (
    <div className="equipment-panel">
      <h2>Equipment</h2>
      
      <div className="equipment-slots">
        <div className="equipment-slot weapon-slot">
          <h3>Weapon</h3>
          {equipment.weapon ? (
            <div className="equipped-item">
              <h4>{equipment.weapon.name}</h4>
              {equipment.weapon.stats && (
                <div className="item-stats">
                  {equipment.weapon.stats.attackBonus !== undefined && 
                    <p>Attack: +{equipment.weapon.stats.attackBonus}</p>}
                  {equipment.weapon.stats.strengthBonus !== undefined && 
                    <p>Strength: +{equipment.weapon.stats.strengthBonus}</p>}
                  {equipment.weapon.stats.defenseBonus !== undefined && 
                    <p>Defense: +{equipment.weapon.stats.defenseBonus}</p>}
                </div>
              )}
            </div>
          ) : (
            <div className="empty-slot">No weapon equipped</div>
          )}
        </div>
        
        <div className="equipment-slot armor-slot">
          <h3>Armor</h3>
          {equipment.armor ? (
            <div className="equipped-item">
              <h4>{equipment.armor.name}</h4>
              {equipment.armor.stats && (
                <div className="item-stats">
                  {equipment.armor.stats.attackBonus !== undefined && 
                    <p>Attack: +{equipment.armor.stats.attackBonus}</p>}
                  {equipment.armor.stats.strengthBonus !== undefined && 
                    <p>Strength: +{equipment.armor.stats.strengthBonus}</p>}
                  {equipment.armor.stats.defenseBonus !== undefined && 
                    <p>Defense: +{equipment.armor.stats.defenseBonus}</p>}
                </div>
              )}
            </div>
          ) : (
            <div className="empty-slot">No armor equipped</div>
          )}
        </div>
      </div>
      
      <div className="equipment-stats">
        <h3>Combat Stats</h3>
        <p>Attack: {calculateTotalStat('attackBonus')}</p>
        <p>Strength: {calculateTotalStat('strengthBonus')}</p>
        <p>Defense: {calculateTotalStat('defenseBonus')}</p>
      </div>
    </div>
  );
  
  function calculateTotalStat(statName: 'attackBonus' | 'strengthBonus' | 'defenseBonus'): number {
    let total = 0;
    
    if (equipment.weapon?.stats?.[statName]) {
      total += equipment.weapon.stats[statName] || 0;
    }
    
    if (equipment.armor?.stats?.[statName]) {
      total += equipment.armor.stats[statName] || 0;
    }
    
    return total;
  }
};

export default EquipmentPanel;
