import React from 'react';
import { Item } from '../../types';

interface CombatStatsProps {
  equipment: {
    weapon?: Item;
    armor?: Item;
  };
}

/**
 * CombatStats component - Calculates and displays combat statistics based on equipped items
 */
const CombatStats: React.FC<CombatStatsProps> = ({ equipment }) => {
  /**
   * Calculate the total value for a specific stat from all equipped items
   */
  const calculateTotalStat = (statName: 'attackBonus' | 'strengthBonus' | 'defenseBonus'): number => {
    let total = 0;

    if (equipment.weapon?.stats?.[statName]) {
      total += equipment.weapon.stats[statName] || 0;
    }

    if (equipment.armor?.stats?.[statName]) {
      total += equipment.armor.stats[statName] || 0;
    }

    return total;
  };

  return (
    <div className="bg-panel border border-panel-border rounded-lg p-4 mt-2">
      <h3 className="text-lg font-medium mb-3 text-text">Combat Stats</h3>
      <div className="grid grid-cols-3 gap-4">
        <div className="bg-item-bg p-3 rounded text-center">
          <div className="text-sm text-gray-600 mb-1">Attack</div>
          <div className="font-semibold text-primary">{calculateTotalStat('attackBonus')}</div>
        </div>
        <div className="bg-item-bg p-3 rounded text-center">
          <div className="text-sm text-gray-600 mb-1">Strength</div>
          <div className="font-semibold text-primary">{calculateTotalStat('strengthBonus')}</div>
        </div>
        <div className="bg-item-bg p-3 rounded text-center">
          <div className="text-sm text-gray-600 mb-1">Defense</div>
          <div className="font-semibold text-primary">{calculateTotalStat('defenseBonus')}</div>
        </div>
      </div>
    </div>
  );
};

export default CombatStats;
