import React from 'react';
import { Item } from '../../types';

interface EquipmentSlotProps {
  type: 'weapon' | 'armor';
  item: Item | undefined;
  onUnequip: () => void;
}

/**
 * EquipmentSlot component - Displays a single equipment slot with its item
 */
const EquipmentSlot: React.FC<EquipmentSlotProps> = ({ type, item, onUnequip }) => {
  const slotTitle = type.charAt(0).toUpperCase() + type.slice(1);

  return (
    <div className="bg-item-bg border border-panel-border rounded-lg p-4">
      <h3 className="text-lg font-medium mb-3 text-text">{slotTitle}</h3>

      {item ? (
        <div className="flex flex-col">
          <h4 className="font-semibold text-text mb-2">{item.name}</h4>
          {item.stats && (
            <div className="space-y-1 mb-3 text-sm">
              {item.stats.attackBonus !== undefined && 
                <p className="text-gray-700">Attack: <span className="text-success">+{item.stats.attackBonus}</span></p>}
              {item.stats.strengthBonus !== undefined && 
                <p className="text-gray-700">Strength: <span className="text-success">+{item.stats.strengthBonus}</span></p>}
              {item.stats.defenseBonus !== undefined && 
                <p className="text-gray-700">Defense: <span className="text-success">+{item.stats.defenseBonus}</span></p>}
            </div>
          )}
          <button 
            className="mt-2 px-4 py-1.5 bg-danger text-white rounded text-sm font-medium hover:bg-danger/90 transition-colors" 
            onClick={onUnequip}
          >
            Unequip
          </button>
        </div>
      ) : (
        <div className="py-6 text-center italic text-gray-500">No {type} equipped</div>
      )}
    </div>
  );
};

export default EquipmentSlot;
