import React, { useState, useEffect } from 'react';
import { useCombat } from '../../hooks/useCombat';
import CombatLog from './CombatLog';

/**
 * CombatPanel component - Handles combat UI and interactions
 */
const CombatPanel: React.FC = () => {
  const {
    enemies,
    currentEnemy,
    isFighting,
    playerStats,
    combatLog,
    combatTick,
    startCombat,
    fleeCombat
  } = useCombat();

  // State for animations and effects
  const [playerAttackAnimation, setPlayerAttackAnimation] = useState(false);
  const [enemyAttackAnimation, setEnemyAttackAnimation] = useState(false);
  const [playerHitSplat, setPlayerHitSplat] = useState<number | null>(null);
  const [enemyHitSplat, setEnemyHitSplat] = useState<number | null>(null);

  // Trigger animations based on combat tick changes
  useEffect(() => {
    if (!isFighting) return;

    // Player attack animation
    setPlayerAttackAnimation(true);
    setTimeout(() => setPlayerAttackAnimation(false), 500);

    // Determine random hit value for display (visual only)
    const potentialPlayerDamage = Math.floor(Math.random() * playerStats.strength) + 1;
    setEnemyHitSplat(potentialPlayerDamage);
    setTimeout(() => setEnemyHitSplat(null), 1000);

    // Enemy attack animation with delay
    setTimeout(() => {
      setEnemyAttackAnimation(true);
      setTimeout(() => setEnemyAttackAnimation(false), 500);

      // Determine random hit value for display (visual only)
      if (currentEnemy) {
        const potentialEnemyDamage = Math.floor(Math.random() * currentEnemy.attack) + 1;
        setPlayerHitSplat(potentialEnemyDamage);
        setTimeout(() => setPlayerHitSplat(null), 1000);
      }
    }, 1000);

  }, [combatTick, isFighting, currentEnemy, playerStats.strength]);

  // Handle enemy selection
  const handleSelectEnemy = (enemyId: string) => {
    if (isFighting) return; // Prevent changing enemy during combat
    startCombat(enemyId);
  };

  // Handle fleeing from combat
  const handleFlee = () => {
    fleeCombat();
  };

  return (
    <div className="p-5 bg-panel rounded-lg shadow-md">
      <h2 className="mt-0 mb-5 text-text border-b border-panel-border pb-2.5 text-xl font-bold">Combat</h2>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-5">
        <div className="md:col-span-1 md:border-r border-panel-border md:pr-5">
          <h3 className="mt-0 mb-4 text-lg font-semibold text-text">Choose an Enemy</h3>
          <div className="flex flex-col gap-3 max-h-[400px] overflow-y-auto pr-2">
            {enemies.map(enemy => (
              <div 
                key={enemy.id}
                className={`bg-item-bg border-2 rounded-md p-3 cursor-pointer transition-all duration-200 hover:translate-y-[-3px] hover:shadow-md
                  ${currentEnemy?.id === enemy.id ? 'border-primary shadow-primary/30' : 'border-item-border'}`}
                onClick={() => handleSelectEnemy(enemy.id)}
              >
                <div className="flex flex-col gap-1.5">
                  <h4 className="font-bold text-base m-0 text-text">{enemy.name}</h4>
                  <div className="flex gap-3 text-sm">
                    <span className="bg-panel-bg px-2 py-0.5 rounded text-text">ATK: {enemy.attack}</span>
                    <span className="bg-panel-bg px-2 py-0.5 rounded text-text">DEF: {enemy.defense}</span>
                    <span className="bg-panel-bg px-2 py-0.5 rounded text-text">HP: {enemy.maxHealth}</span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        <div className="md:col-span-2">
          <h3 className="mt-0 mb-4 text-lg font-semibold text-text">Combat Status</h3>
          {currentEnemy ? (
            <div className="flex flex-col gap-4">
              <h4 className="mt-0 mb-2.5 text-base font-semibold text-text">Fighting: {currentEnemy.name}</h4>

              <div className="flex justify-between items-center p-[30px_20px] bg-item-bg rounded-lg">
                <div className={`relative flex flex-col items-center ${playerAttackAnimation ? 'animate-[player-attack_0.5s_ease-in-out]' : ''}`}>
                  <div className="text-4xl">üßô</div>
                  {playerHitSplat !== null && (
                    <div className="absolute -top-4 -right-4 bg-danger text-white rounded-full w-8 h-8 flex items-center justify-center font-bold animate-[hit-splat_0.5s_ease-in-out]">
                      {playerHitSplat}
                    </div>
                  )}
                </div>

                <div className="text-xl font-bold text-text">VS</div>

                <div className={`relative flex flex-col items-center ${enemyAttackAnimation ? 'animate-[enemy-attack_0.5s_ease-in-out]' : ''}`}>
                  <div className="text-4xl">
                    {currentEnemy.id.includes('goblin') ? 'üë∫' : 
                     currentEnemy.id.includes('rat') ? 'üêÄ' : 
                     currentEnemy.id.includes('bandit') ? 'ü•∑' : 'üëæ'}
                  </div>
                  {enemyHitSplat !== null && (
                    <div className="absolute -top-4 -left-4 bg-success text-white rounded-full w-8 h-8 flex items-center justify-center font-bold animate-[hit-splat_0.5s_ease-in-out]">
                      {enemyHitSplat}
                    </div>
                  )}
                </div>
              </div>

              <div className="flex flex-col gap-3 mb-5">
                <div className="flex flex-col gap-1">
                  <label className="text-sm font-bold text-text">Enemy Health:</label>
                  <div className="h-2.5 bg-gray-200 rounded-full overflow-hidden">
                    <div 
                      className="h-full bg-danger transition-all duration-500"
                      style={{ width: `${(currentEnemy.health / currentEnemy.maxHealth) * 100}%` }}
                    ></div>
                  </div>
                  <div className="text-sm text-right text-text">{currentEnemy.health} / {currentEnemy.maxHealth}</div>
                </div>

                <div className="flex flex-col gap-1">
                  <label className="text-sm font-bold text-text">Your Health:</label>
                  <div className="h-2.5 bg-gray-200 rounded-full overflow-hidden">
                    <div 
                      className="h-full bg-success transition-all duration-500"
                      style={{ width: `${(playerStats.health / playerStats.maxHealth) * 100}%` }}
                    ></div>
                  </div>
                  <div className="text-sm text-right text-text">{Math.floor(playerStats.health)} / {playerStats.maxHealth}</div>
                </div>
              </div>

              <div className="mb-4">
                <h4 className="mt-0 mb-2.5 text-base font-semibold text-text">Your Combat Stats</h4>
                <div className="grid grid-cols-3 gap-2">
                  <div className="flex flex-col">
                    <span className="text-sm font-medium text-text-secondary">Attack:</span>
                    <span className="text-text">{playerStats.attack}</span>
                  </div>
                  <div className="flex flex-col">
                    <span className="text-sm font-medium text-text-secondary">Strength:</span>
                    <span className="text-text">{playerStats.strength}</span>
                  </div>
                  <div className="flex flex-col">
                    <span className="text-sm font-medium text-text-secondary">Defense:</span>
                    <span className="text-text">{playerStats.defense}</span>
                  </div>
                </div>
              </div>

              <div className="mb-4">
                <button 
                  className={`bg-danger text-white border-0 py-2 px-4 rounded font-bold transition-colors duration-200
                    ${!isFighting ? 'bg-gray-400 cursor-not-allowed' : 'hover:bg-red-600'}`}
                  onClick={handleFlee}
                  disabled={!isFighting}
                >
                  Flee from Combat
                </button>
              </div>

              <div className="text-sm text-text-secondary">
                <p className="mb-1">Combat is automatic. Your character will attack every 2 seconds.</p>
                <p className="mb-0">Successfully landing hits will earn you Combat XP.</p>
              </div>
            </div>
          ) : (
            <div className="flex justify-center items-center h-[200px] bg-item-bg rounded-lg italic text-text opacity-70">
              <p>Select an enemy from the list to begin combat.</p>
            </div>
          )}

          <div className="mt-5 pt-5 border-t border-panel-border">
            <h3 className="mt-0 mb-4 text-lg font-semibold text-text">Combat Log</h3>
            <CombatLog logs={combatLog} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default CombatPanel;
