import React, { useState } from 'react';
import { useSkills } from '../../hooks/useSkills';
import SkillProgressBar from './SkillProgressBar';
import { Skill } from '../../types';
import { useGameContext } from '../../context/GameContext';

/**
 * SkillPanel component - Displays player skills and their progression
 */
const SkillPanel: React.FC = () => {
  const { skills, currentSkill, selectSkill, startSkill, stopSkill } = useSkills();
  const { loading } = useGameContext();
  const [expandedSkillId, setExpandedSkillId] = useState<string | null>(null);

  /**
   * Handler to toggle skill activity state
   */
  const handleToggleSkill = (skill: Skill, e: React.MouseEvent) => {
    e.stopPropagation(); // Prevent triggering the skill selection
    if (skill.isActive) {
      stopSkill(skill.id);
    } else {
      startSkill(skill.id);
    }
  };

  const toggleSkillDetails = (skillId: string) => {
    setExpandedSkillId(expandedSkillId === skillId ? null : skillId);
    selectSkill(skillId);
  };

  // Calculate action progress percentage
  const getActionProgress = (skill: Skill): number => {
    if (!skill.isActive || !skill.actionProgress) return 0;

    // Calculate percentage of action completion
    return Math.min(Math.floor((skill.actionProgress || 0) * 100), 100);
  };

  // Show loading state
  if (loading) {
    return (
      <div className="bg-panel border border-panel-border rounded-lg p-4 shadow">
        <h2 className="text-xl font-semibold mb-4">Skills</h2>
        <div className="flex items-center justify-center h-40 text-gray-500 italic">
          <div className="animate-spin mr-2 h-5 w-5 border-2 border-primary border-t-transparent rounded-full"></div>
          Loading skills...
        </div>
      </div>
    );
  }

  // Handle case where skills might not be available
  if (!skills || Object.keys(skills).length === 0) {
    return (
      <div className="bg-panel border border-panel-border rounded-lg p-4 shadow">
        <h2 className="text-xl font-semibold mb-4">Skills</h2>
        <div className="flex items-center justify-center h-40 text-gray-500 italic">
          No skills available yet
        </div>
      </div>
    );
  }

  return (
    <div className="bg-panel border border-panel-border rounded-lg p-4 shadow">
      <h2 className="text-xl font-semibold mb-4">Skills</h2>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 max-h-[75vh] overflow-y-auto p-2">
        {Object.values(skills).filter(Boolean).map((skill: Skill) => (
          <div 
            key={skill.id} 
            className={`bg-panel border rounded-lg p-4 cursor-pointer transition-all duration-200 hover:shadow-md hover:-translate-y-1 ${
              currentSkill?.id === skill.id ? 'border-primary shadow-sm' : 'border-panel-border'
            }`}
            onClick={() => toggleSkillDetails(skill.id)}
            tabIndex={0}
            aria-label={`${skill.name} skill, level ${skill.level}`}
            onKeyDown={(e) => e.key === 'Enter' && toggleSkillDetails(skill.id)}
          >
            <div className="flex justify-between items-center mb-2">
              <div className="font-medium text-lg">{skill.name}</div>
              <span className="bg-primary text-white text-xs font-semibold px-2 py-1 rounded-full">
                Level {skill.level}
              </span>
            </div>

            <SkillProgressBar skill={skill} />

            {/* Add action progress bar for active skills */}
            {skill.isActive && (
              <div className="mt-3">
                <div className="text-sm text-gray-600 mb-1">Action progress:</div>
                <div className="w-full h-2 bg-gray-200 rounded-full overflow-hidden">
                  <div 
                    className="h-full bg-warning animate-pulse"
                    style={{ width: `${getActionProgress(skill)}%` }} 
                  />
                </div>
              </div>
            )}

            <div className="flex justify-end mt-3">
              <button
                className={`px-4 py-1.5 rounded text-sm font-medium transition-colors ${
                  skill.isActive 
                    ? 'bg-danger text-white hover:bg-danger/90' 
                    : 'bg-primary text-white hover:bg-primary-dark'
                }`}
                onClick={(e) => handleToggleSkill(skill, e)}
                aria-label={skill.isActive ? `Stop ${skill.name}` : `Start ${skill.name}`}
              >
                {skill.isActive ? 'Stop' : 'Start'}
              </button>
            </div>

            {expandedSkillId === skill.id && (
              <div className="mt-4 pt-3 border-t border-gray-200">
                <div className="grid grid-cols-2 gap-2 mb-2">
                  <div className="text-sm">
                    <span className="text-gray-500">XP per action:</span> 
                    <span className="font-medium ml-1">{skill.xpPerAction}</span>
                  </div>
                  <div className="text-sm">
                    <span className="text-gray-500">Status:</span> 
                    <span className={`font-medium ml-1 ${skill.isActive ? 'text-success' : 'text-gray-600'}`}>
                      {skill.isActive ? 'Active' : 'Inactive'}
                    </span>
                  </div>
                </div>

                {skill.mastery && skill.mastery.unlocks && Object.keys(skill.mastery.unlocks).length > 0 && (
                  <div className="mt-3">
                    <h4 className="text-sm font-semibold mb-2">Mastery Unlocks</h4>
                    <ul className="text-sm space-y-1.5">
                      {Object.entries(skill.mastery.unlocks).map(([level, description]) => (
                        <li 
                          key={level}
                          className={`py-1 border-b border-gray-100 ${
                            skill.level >= parseInt(level) 
                              ? 'text-gray-800' 
                              : 'text-gray-400'
                          }`}
                        >
                          <span className={`inline-block w-16 font-medium ${
                            skill.level >= parseInt(level) ? 'text-primary' : ''
                          }`}>
                            Level {level}:
                          </span> 
                          {description}
                        </li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default SkillPanel;
