import React from 'react';
import { Item } from '../../types';

interface InventoryItemProps {
  item: Item;
  selectedQuantity: number;
  onSell: () => void;
  onEquip: () => void;
}

/**
 * InventoryItem component - Displays a single item in the inventory
 * with actions like sell and equip based on item type
 */
const InventoryItem: React.FC<InventoryItemProps> = ({ 
  item, 
  selectedQuantity,
  onSell,
  onEquip
}) => {
  const canSell = 
    item.sellPrice !== undefined && 
    item.sellPrice > 0 && 
    selectedQuantity <= item.quantity;

  const canEquip = 
    item.type === 'equipment' && 
    item.slot !== undefined;

  // Calculate total sell value
  const sellValue = canSell ? (item.sellPrice! * selectedQuantity) : 0;

  return (
    <div className={`bg-item-bg border border-panel-border rounded-lg p-3 ${
      item.type === 'equipment' ? 'border-l-4 border-l-primary' : ''
    }`}>
      <div className="flex justify-between items-center mb-2">
        <h4 className="font-medium text-text">{item.name}</h4>
        <span className="bg-gray-200 text-gray-700 px-2 py-0.5 rounded-full text-xs font-medium">
          Ã—{item.quantity}
        </span>
      </div>

      {item.type === 'equipment' && item.stats && (
        <div className="space-y-1 mb-3 text-xs text-gray-600">
          {item.slot && <p className="font-medium">Type: {item.slot}</p>}
          {item.stats.attackBonus && <p>Attack: <span className="text-success">+{item.stats.attackBonus}</span></p>}
          {item.stats.strengthBonus && <p>Strength: <span className="text-success">+{item.stats.strengthBonus}</span></p>}
          {item.stats.defenseBonus && <p>Defense: <span className="text-success">+{item.stats.defenseBonus}</span></p>}
        </div>
      )}

      <div className="mt-3 space-y-2">
        {canEquip && (
          <button 
            className="w-full px-3 py-1.5 bg-primary text-white text-sm font-medium rounded hover:bg-primary-dark transition-colors"
            onClick={onEquip}
          >
            Equip
          </button>
        )}

        {canSell && (
          <div className="space-y-1">
            <p className="text-xs text-gray-600">
              Sell price: <span className="text-warning font-medium">{sellValue} gold</span> 
              {selectedQuantity > 1 && <span className="text-gray-500"> ({item.sellPrice} each)</span>}
            </p>
            <button 
              className="w-full px-3 py-1.5 bg-gray-200 text-gray-700 text-sm font-medium rounded hover:bg-gray-300 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
              onClick={onSell}
              disabled={!canSell}
            >
              Sell {selectedQuantity}
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default InventoryItem;
