import React, { useState, useEffect } from 'react';
import { Item } from '../types';

interface ShopProps {
  shopItems: Item[];
  playerGold: number;
  onBuyItem: (itemId: string, quantity: number) => void;
  isLoading?: boolean;
  error?: string | null;
}

const Shop: React.FC<ShopProps> = ({ 
  shopItems, 
  playerGold, 
  onBuyItem, 
  isLoading = false, 
  error = null 
}) => {
  const [selectedItem, setSelectedItem] = useState<string>('');
  const [quantity, setQuantity] = useState<number>(1);
  const [localError, setLocalError] = useState<string | null>(null);

  // Reset local error when prop error changes
  useEffect(() => {
    if (error) {
      setLocalError(error);
    }
  }, [error]);

  const handleBuyItem = (itemId: string) => {
    if (canAffordItem(itemId)) {
      onBuyItem(itemId, quantity);
      // We don't reset quantity here to make it easier for users to buy multiple batches
    }
  };

  const handleQuantityChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = parseInt(e.target.value);
    if (!isNaN(value) && value > 0) {
      setQuantity(value);
    } else {
      setQuantity(1);
    }
  };

  const canAffordItem = (itemId: string): boolean => {
    const item = shopItems.find(item => item.id === itemId);
    return item && item.buyPrice !== undefined ? playerGold >= item.buyPrice * quantity : false;
  };

  const renderErrorMessage = () => {
    if (!localError) return null;

    return (
      <div className="bg-danger/10 border border-danger rounded p-3 mb-4 relative">
        {localError}
        <button 
          onClick={() => setLocalError(null)} 
          className="absolute top-1 right-2 text-danger hover:text-danger-dark"
          aria-label="Close error message"
        >
          Ã—
        </button>
      </div>
    );
  };

  return (
    <div className="space-y-4">
      <div className="flex justify-between items-center">
        <p className="text-lg font-medium">Your gold: <span className="text-warning">{playerGold}</span></p>
      </div>

      <div className="flex items-center space-x-3 mb-4">
        <label htmlFor="quantity" className="text-sm text-gray-600">Quantity:</label>
        <input
          type="number"
          id="quantity"
          min="1"
          value={quantity}
          onChange={handleQuantityChange}
          disabled={isLoading}
          className="w-20 p-1.5 border border-panel-border rounded text-center focus:border-primary focus:ring-1 focus:ring-primary outline-none"
        />
      </div>

      {renderErrorMessage()}

      {isLoading ? (
        <div className="flex items-center justify-center p-6 text-gray-500">
          <div className="animate-spin mr-2 h-5 w-5 border-2 border-primary border-t-transparent rounded-full"></div>
          Processing your purchase...
        </div>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
          {shopItems.map(item => (
            <div 
              key={item.id} 
              className="bg-item-bg border border-panel-border rounded-lg p-4 hover:shadow-md transition-shadow"
            >
              <h3 className="font-semibold text-text mb-2">{item.name}</h3>
              <div className="space-y-1 text-sm">
                <p className="text-gray-600">Price: <span className="text-warning font-medium">{item.buyPrice ?? 'N/A'} gold each</span></p>
                <p className="text-gray-600">Total: <span className="text-warning font-medium">{item.buyPrice !== undefined ? item.buyPrice * quantity : 'N/A'} gold</span></p>
              </div>

              {item.stats && (
                <div className="mt-3 p-2 bg-panel rounded space-y-1 text-sm">
                  {item.stats.attackBonus && <p className="text-gray-700">Attack: <span className="text-success">+{item.stats.attackBonus}</span></p>}
                  {item.stats.strengthBonus && <p className="text-gray-700">Strength: <span className="text-success">+{item.stats.strengthBonus}</span></p>}
                  {item.stats.defenseBonus && <p className="text-gray-700">Defense: <span className="text-success">+{item.stats.defenseBonus}</span></p>}
                </div>
              )}

              <button
                onClick={() => handleBuyItem(item.id)}
                disabled={!canAffordItem(item.id) || isLoading}
                className={`w-full mt-3 px-4 py-2 rounded text-sm font-medium transition-colors ${
                  canAffordItem(item.id) && !isLoading
                    ? 'bg-primary text-white hover:bg-primary-dark' 
                    : 'bg-gray-300 text-gray-500 cursor-not-allowed'
                }`}
              >
                Buy {quantity}
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Shop;

// Add CSS styles for better layout
