// App.tsx
import React, { useState, useEffect } from 'react';
import SkillList from './components/skill-list-component';
import CombatPanel from './components/combat-panel-component';
import Inventory from './components/inventory-component';
import Shop from './components/shop-component';
import Equipment from './components/equipment-component';
import './App.css';
import { GameState } from './types';

function App() {
  const [gameState, setGameState] = useState<GameState | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // In a real app, you would fetch from your API
    const fetchGameData = async () => {
      try {
        // Mock API call
        setTimeout(() => {
          const mockData: GameState = {
            player: {
              id: 'player1',
              gold: 100,
              lastUpdate: Date.now(),
              skills: {},
              inventory: {},
              equipment: {},
              combat: {
                isFighting: false
              }
            }
          };
          setGameState(mockData);
          setLoading(false);
        }, 500);
      } catch (error) {
        console.error('Error fetching game data:', error);
        setLoading(false);
      }
    };

    fetchGameData();
  }, []);

  const handleStartSkill = (skillId: string) => {
    console.log('Starting skill:', skillId);
    // Implement skill starting logic
  };

  const handleStopSkill = (skillId: string) => {
    console.log('Stopping skill:', skillId);
    // Implement skill stopping logic
  };

  if (loading || !gameState) {
    return <div className="loading">Loading game data...</div>;
  }

  return (
    <div className="app">
      <header className="app-header">
        <h1>Melvor Idle Replica</h1>
        <div className="player-gold">Gold: {gameState.player.gold}</div>
      </header>
      <main className="app-main">
        <div className="game-container">
          <div className="left-panel">
            <SkillList 
              skills={gameState.player.skills}
              onStartSkill={handleStartSkill}
              onStopSkill={handleStopSkill}
            />
          </div>
          <div className="center-panel">
            <CombatPanel 
              isFighting={gameState.player.combat.isFighting}
              currentEnemy={gameState.player.combat.currentEnemy}
              playerAttack={0}
              playerStrength={0}
              playerDefense={0}
              playerHitpoints={100}
              availableEnemies={[]}
              onStartCombat={() => {}}
              onStopCombat={() => {}}
            />
          </div>
          <div className="right-panel">
            <Equipment 
              weapon={gameState.player.equipment.weapon}
              armor={gameState.player.equipment.armor}
              onUnequipItem={() => {}}
            />
            <Inventory 
              inventory={gameState.player.inventory}
              onSellItem={() => {}}
              onEquipItem={() => {}}
            />
            <Shop 
              shopItems={[]}
              playerGold={gameState.player.gold}
              onBuyItem={() => {}}
            />
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
