import React, { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import { GameState, Item, Skill, Mastery } from '../types';
import { ENEMIES, SKILL_IDS, ITEMS, ITEM_IDS } from '../constants';

interface GameContextProps {
  gameState: GameState | null;
  loading: boolean;
  updateGameState: (updater: (prevState: GameState | null) => GameState | null) => void;
}

const GameContext = createContext<GameContextProps | undefined>(undefined);

export const useGameContext = () => {
  const context = useContext(GameContext);
  if (context === undefined) {
    throw new Error('useGameContext must be used within a GameProvider');
  }
  return context;
};

interface GameProviderProps {
  children: ReactNode;
}

export const GameProvider: React.FC<GameProviderProps> = ({ children }) => {
  const [gameState, setGameState] = useState<GameState | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // In a real app, you would fetch from your API
    const fetchGameData = async () => {
      try {
        // Mock API call with simulated delay
        setTimeout(() => {
          // Create mock skills
          const mockSkills: { [id: string]: Skill } = {
            [SKILL_IDS.WOODCUTTING]: {
              id: SKILL_IDS.WOODCUTTING,
              name: 'Woodcutting',
              level: 1,
              xp: 0,
              xpPerAction: 5,
              isActive: false,
              mastery: {
                level: 1,
                xp: 0,
                unlocks: {
                  '5': 'Faster woodcutting speed',
                  '10': 'Double logs chance (10%)'
                }
              }
            },
            [SKILL_IDS.FISHING]: {
              id: SKILL_IDS.FISHING,
              name: 'Fishing',
              level: 1,
              xp: 0,
              xpPerAction: 7,
              isActive: false,
              mastery: {
                level: 1,
                xp: 0,
                unlocks: {
                  '5': 'Better fish quality',
                  '10': 'Double fish chance (10%)'
                }
              }
            },
            [SKILL_IDS.MINING]: {
              id: SKILL_IDS.MINING,
              name: 'Mining',
              level: 1,
              xp: 0,
              xpPerAction: 6,
              isActive: false,
              mastery: {
                level: 1,
                xp: 0,
                unlocks: {}
              }
            }
          };

          // Convert ITEMS to proper Item type with correct slot type
          const itemsWithProperType: { [id: string]: Item } = {};
          
          Object.entries(ITEMS).forEach(([key, item]) => {
            // Create a new item object with properly typed properties
            const convertedItem: Item = {
              id: item.id,
              name: item.name,
              quantity: item.quantity,
              type: item.type as 'resource' | 'equipment',
              sellPrice: item.sellPrice,
              buyPrice: item.buyPrice,
              stats: item.stats,
              // Only include slot if it's either 'weapon' or 'armor'
              slot: (item.slot === 'weapon' || item.slot === 'armor') ? item.slot : undefined,
              craftingRecipe: item.craftingRecipe
            };
            
            itemsWithProperType[key] = convertedItem;
          });

          // Create mock inventory
          const mockInventory: { [id: string]: Item } = {
            [ITEM_IDS.LOGS]: { ...itemsWithProperType[ITEM_IDS.LOGS], quantity: 5 },
            [ITEM_IDS.RAW_FISH]: { ...itemsWithProperType[ITEM_IDS.RAW_FISH], quantity: 3 }
          };

          const mockData: GameState = {
            player: {
              id: 'player1',
              gold: 100,
              lastUpdate: Date.now(),
              skills: mockSkills,
              inventory: mockInventory,
              equipment: {},
              combat: {
                isFighting: false
              }
            }
          };
          
          setGameState(mockData);
          setLoading(false);
        }, 500);
      } catch (error) {
        console.error('Error fetching game data:', error);
        setLoading(false);
      }
    };

    fetchGameData();
  }, []);

  const updateGameState = (updater: (prevState: GameState | null) => GameState | null) => {
    setGameState(updater);
  };

  return (
    <GameContext.Provider value={{ gameState, loading, updateGameState }}>
      {children}
    </GameContext.Provider>
  );
};
