import React, { useState, useEffect } from 'react';
import { useCombat } from '../../hooks/useCombat';
import './CombatPanel.css';
import CombatLog from './CombatLog';

/**
 * CombatPanel component - Handles combat UI and interactions
 */
const CombatPanel: React.FC = () => {
  const {
    enemies,
    currentEnemy,
    isFighting,
    playerStats,
    combatLog,
    combatTick,
    startCombat,
    fleeCombat
  } = useCombat();

  // State for animations and effects
  const [playerAttackAnimation, setPlayerAttackAnimation] = useState(false);
  const [enemyAttackAnimation, setEnemyAttackAnimation] = useState(false);
  const [playerHitSplat, setPlayerHitSplat] = useState<number | null>(null);
  const [enemyHitSplat, setEnemyHitSplat] = useState<number | null>(null);

  // Trigger animations based on combat tick changes
  useEffect(() => {
    if (!isFighting) return;

    // Player attack animation
    setPlayerAttackAnimation(true);
    setTimeout(() => setPlayerAttackAnimation(false), 500);

    // Determine random hit value for display (visual only)
    const potentialPlayerDamage = Math.floor(Math.random() * playerStats.strength) + 1;
    setEnemyHitSplat(potentialPlayerDamage);
    setTimeout(() => setEnemyHitSplat(null), 1000);

    // Enemy attack animation with delay
    setTimeout(() => {
      setEnemyAttackAnimation(true);
      setTimeout(() => setEnemyAttackAnimation(false), 500);

      // Determine random hit value for display (visual only)
      if (currentEnemy) {
        const potentialEnemyDamage = Math.floor(Math.random() * currentEnemy.attack) + 1;
        setPlayerHitSplat(potentialEnemyDamage);
        setTimeout(() => setPlayerHitSplat(null), 1000);
      }
    }, 1000);

  }, [combatTick, isFighting, currentEnemy, playerStats.strength]);

  // Handle enemy selection
  const handleSelectEnemy = (enemyId: string) => {
    if (isFighting) return; // Prevent changing enemy during combat
    startCombat(enemyId);
  };

  // Handle fleeing from combat
  const handleFlee = () => {
    fleeCombat();
  };

  return (
    <div className="combat-panel">
      <h2>Combat</h2>

      <div className="combat-container">
        <div className="enemy-selection">
          <h3>Choose an Enemy</h3>
          <div className="enemy-list">
            {enemies.map(enemy => (
              <div 
                key={enemy.id}
                className={`enemy-card ${currentEnemy?.id === enemy.id ? 'selected' : ''}`}
                onClick={() => handleSelectEnemy(enemy.id)}
              >
                <div className="enemy-info">
                  <h4 className="enemy-name">{enemy.name}</h4>
                  <div className="enemy-stats">
                    <span className="stat">ATK: {enemy.attack}</span>
                    <span className="stat">DEF: {enemy.defense}</span>
                    <span className="stat">HP: {enemy.maxHealth}</span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        <div className="combat-status">
          <h3>Combat Status</h3>
          {currentEnemy ? (
            <div className="selected-enemy-details">
              <h4>Fighting: {currentEnemy.name}</h4>

              <div className="combat-arena">
                <div className={`player-character ${playerAttackAnimation ? 'attack' : ''}`}>
                  <div className="character-icon">üßô</div>
                  {playerHitSplat !== null && (
                    <div className="hit-splat player-hit">
                      {playerHitSplat}
                    </div>
                  )}
                </div>

                <div className="vs-indicator">VS</div>

                <div className={`enemy-character ${enemyAttackAnimation ? 'attack' : ''}`}>
                  <div className="character-icon">
                    {currentEnemy.id.includes('goblin') ? 'üë∫' : 
                     currentEnemy.id.includes('rat') ? 'üêÄ' : 
                     currentEnemy.id.includes('bandit') ? 'ü•∑' : 'üëæ'}
                  </div>
                  {enemyHitSplat !== null && (
                    <div className="hit-splat enemy-hit">
                      {enemyHitSplat}
                    </div>
                  )}
                </div>
              </div>

              <div className="health-bars">
                <div className="health-bar-container">
                  <label>Enemy Health:</label>
                  <div className="health-bar">
                    <div 
                      className="health-fill enemy-health" 
                      style={{ width: `${(currentEnemy.health / currentEnemy.maxHealth) * 100}%` }}
                    ></div>
                  </div>
                  <div className="health-text">{currentEnemy.health} / {currentEnemy.maxHealth}</div>
                </div>

                <div className="health-bar-container">
                  <label>Your Health:</label>
                  <div className="health-bar">
                    <div 
                      className="health-fill player-health" 
                      style={{ width: `${(playerStats.health / playerStats.maxHealth) * 100}%` }}
                    ></div>
                  </div>
                  <div className="health-text">{Math.floor(playerStats.health)} / {playerStats.maxHealth}</div>
                </div>
              </div>

              <div className="combat-stats-display">
                <h4>Your Combat Stats</h4>
                <div className="stats-grid">
                  <div className="stat-item">
                    <span className="stat-label">Attack:</span>
                    <span className="stat-value">{playerStats.attack}</span>
                  </div>
                  <div className="stat-item">
                    <span className="stat-label">Strength:</span>
                    <span className="stat-value">{playerStats.strength}</span>
                  </div>
                  <div className="stat-item">
                    <span className="stat-label">Defense:</span>
                    <span className="stat-value">{playerStats.defense}</span>
                  </div>
                </div>
              </div>

              <div className="combat-controls">
                <button 
                  className="flee-button"
                  onClick={handleFlee}
                  disabled={!isFighting}
                >
                  Flee from Combat
                </button>
              </div>

              <div className="combat-info">
                <p>Combat is automatic. Your character will attack every 2 seconds.</p>
                <p>Successfully landing hits will earn you Combat XP.</p>
              </div>
            </div>
          ) : (
            <div className="no-enemy-selected">
              <p>Select an enemy from the list to begin combat.</p>
            </div>
          )}
        </div>

        <div className="combat-log-container">
          <h3>Combat Log</h3>
          <CombatLog logs={combatLog} />
        </div>
      </div>
    </div>
  );
};

export default CombatPanel;
