import React, { useState } from 'react';
import { useInventory } from '../../hooks/useInventory';
import { Item } from '../../types';

const InventoryPanel: React.FC = () => {
  const { inventory, sellItem, equipItem } = useInventory();
  const [selectedQuantity, setSelectedQuantity] = useState<number>(1);
  
  const handleSellItem = (itemId: string) => {
    sellItem(itemId, selectedQuantity);
  };
  
  const handleEquipItem = (itemId: string) => {
    equipItem(itemId);
  };
  
  return (
    <div className="inventory-panel">
      <h2>Inventory</h2>
      
      <div className="quantity-selector">
        <label htmlFor="quantity">Quantity: </label>
        <input
          type="number"
          id="quantity"
          min={1}
          max={99}
          value={selectedQuantity}
          onChange={(e) => setSelectedQuantity(Math.max(1, parseInt(e.target.value) || 1))}
        />
      </div>
      
      <div className="inventory-grid">
        {Object.keys(inventory).length === 0 ? (
          <p className="empty-inventory">Your inventory is empty.</p>
        ) : (
          Object.keys(inventory).map(itemId => {
            const item = inventory[itemId];
            return (
              <div key={itemId} className="inventory-item">
                <h4>{item.name}</h4>
                <p>Quantity: {item.quantity}</p>
                {item.type === 'equipment' && (
                  <button 
                    className="equip-btn"
                    onClick={() => handleEquipItem(itemId)}
                  >
                    Equip
                  </button>
                )}
                {item.sellPrice && (
                  <div className="sell-controls">
                    <p>Sell price: {item.sellPrice} gold</p>
                    <button 
                      className="sell-btn"
                      onClick={() => handleSellItem(itemId)}
                      disabled={selectedQuantity > item.quantity}
                    >
                      Sell {selectedQuantity}
                    </button>
                  </div>
                )}
              </div>
            );
          })
        )}
      </div>
    </div>
  );
};

export default InventoryPanel;
