import React, { useState } from 'react';
import { useShop } from '../../hooks/useShop';
import { ITEMS } from '../../constants';
import { Item } from '../../types';

const ShopPanel: React.FC = () => {
  const { gold, buyItem } = useShop();
  const [selectedQuantity, setSelectedQuantity] = useState<number>(1);
  
  const shopItems = Object.keys(ITEMS)
    .map(id => ITEMS[id])
    .filter(item => item.buyPrice !== undefined);
  
  const handleBuyItem = (item: Item) => {
    buyItem(item, selectedQuantity);
  };
  
  return (
    <div className="shop-panel">
      <h2>Shop</h2>
      <p className="gold-display">Your gold: {gold}</p>
      
      <div className="quantity-selector">
        <label htmlFor="shop-quantity">Quantity: </label>
        <input
          type="number"
          id="shop-quantity"
          min={1}
          max={99}
          value={selectedQuantity}
          onChange={(e) => setSelectedQuantity(Math.max(1, parseInt(e.target.value) || 1))}
        />
      </div>
      
      <div className="shop-items">
        {shopItems.length === 0 ? (
          <p className="empty-shop">No items available.</p>
        ) : (
          shopItems.map(item => {
            const totalCost = (item.buyPrice || 0) * selectedQuantity;
            const canAfford = gold >= totalCost;
            
            return (
              <div key={item.id} className="shop-item">
                <h4>{item.name}</h4>
                <p>Price: {item.buyPrice} gold each</p>
                <p>Total: {totalCost} gold</p>
                {item.type === 'equipment' && item.stats && (
                  <div className="item-stats">
                    <p>Type: {item.slot}</p>
                    {item.stats.attackBonus !== undefined && <p>Attack: +{item.stats.attackBonus}</p>}
                    {item.stats.strengthBonus !== undefined && <p>Strength: +{item.stats.strengthBonus}</p>}
                    {item.stats.defenseBonus !== undefined && <p>Defense: +{item.stats.defenseBonus}</p>}
                  </div>
                )}
                <button 
                  className="buy-btn"
                  onClick={() => handleBuyItem(item)}
                  disabled={!canAfford}
                >
                  {canAfford ? `Buy ${selectedQuantity}` : 'Not enough gold'}
                </button>
              </div>
            );
          })
        )}
      </div>
    </div>
  );
};

export default ShopPanel;
