import React, { useState } from 'react';
import { useSkills } from '../../hooks/useSkills';
import SkillProgressBar from './SkillProgressBar';
import { Skill } from '../../types';
import { useGameContext } from '../../context/GameContext';

// Skill emoji mapping
const SKILL_EMOJIS: Record<string, string> = {
  mining: '⛏️',
  woodcutting: '🪓',
  fishing: '🎣',
  cooking: '🍳',
  crafting: '🧵',
  smithing: '🔨',
  farming: '🌱',
  combat: '⚔️',
  magic: '✨',
  default: '📊'
};

/**
 * SkillPanel component - Displays player skills and their progression
 * Optimized for desktop-only viewing
 */
const SkillPanel: React.FC = () => {
  const { skills, currentSkill, selectSkill, startSkill, stopSkill } = useSkills();
  const { loading } = useGameContext();
  const [expandedSkillId, setExpandedSkillId] = useState<string | null>(null);

  /**
   * Handler to toggle skill activity state
   */
  const handleToggleSkill = (skill: Skill, e: React.MouseEvent) => {
    e.stopPropagation(); // Prevent triggering the skill selection
    if (skill.isActive) {
      stopSkill(skill.id);
    } else {
      startSkill(skill.id);
    }
  };

  /**
   * Toggle expanded skill details view
   */
  const toggleSkillDetails = (skillId: string) => {
    setExpandedSkillId(expandedSkillId === skillId ? null : skillId);
    selectSkill(skillId);
  };

  /**
   * Get skill emoji based on skill ID/name
   */
  const getSkillEmoji = (skill: Skill): string => {
    for (const [key, emoji] of Object.entries(SKILL_EMOJIS)) {
      if (skill.id.toLowerCase().includes(key) || skill.name.toLowerCase().includes(key)) {
        return emoji;
      }
    }
    return SKILL_EMOJIS.default;
  };

  // Show loading state
  if (loading) {
    return (
      <div className="bg-panel rounded-lg shadow-md border border-panel-border h-full overflow-hidden">
        <div className="px-5 py-4 bg-header-bg text-header-text">
          <h2 className="m-0 text-xl font-bold flex items-center">
            <span className="mr-2">📊</span> Skills
          </h2>
        </div>

        <div className="flex items-center justify-center h-full text-text-secondary p-8">
          <div className="flex flex-col items-center">
            <div className="animate-spin h-8 w-8 border-3 border-primary border-t-transparent rounded-full mb-4"></div>
            <span className="text-sm">Loading skills...</span>
          </div>
        </div>
      </div>
    );
  }

  // Handle case where skills might not be available
  if (!skills || Object.keys(skills).length === 0) {
    return (
      <div className="bg-panel rounded-lg shadow-md border border-panel-border h-full overflow-hidden">
        <div className="px-5 py-4 bg-header-bg text-header-text">
          <h2 className="m-0 text-xl font-bold flex items-center">
            <span className="mr-2">📊</span> Skills
          </h2>
        </div>

        <div className="flex items-center justify-center h-full text-text-secondary p-8">
          <div className="flex flex-col items-center text-center">
            <div className="text-4xl mb-4">📋</div>
            <p className="mb-2 font-medium">No skills available yet</p>
            <p className="text-sm opacity-75">Check back soon for more content!</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-panel rounded-lg shadow-md border border-panel-border h-full overflow-hidden">
      <div className="px-5 py-4 bg-header-bg text-header-text">
        <h2 className="m-0 text-xl font-bold flex items-center">
          <span className="mr-2">📊</span> Skills
        </h2>
      </div>

      <div className="p-4">
        <div className="flex flex-col gap-4 max-h-[calc(100vh-200px)] overflow-y-auto pr-1 custom-scrollbar">
          {Object.values(skills).filter(Boolean).map((skill: Skill) => (
            <div 
              key={skill.id} 
              className={`bg-item-bg border rounded-lg overflow-hidden transition-all duration-200 hover:shadow-md ${
                currentSkill?.id === skill.id ? 'border-primary shadow-md' : 'border-item-border'
              }`}
            >
              <div 
                className="cursor-pointer"
                onClick={() => toggleSkillDetails(skill.id)}
                tabIndex={0}
                aria-label={`${skill.name} skill, level ${skill.level}`}
                onKeyDown={(e) => e.key === 'Enter' && toggleSkillDetails(skill.id)}
              >
                <div className="p-4">
                  <div className="flex justify-between items-center mb-3">
                    <div className="flex items-center">
                      <div className="w-10 h-10 rounded-full bg-panel-bg flex items-center justify-center mr-3 text-xl">
                        {getSkillEmoji(skill)}
                      </div>
                      <div>
                        <div className="font-semibold text-base text-text">{skill.name}</div>
                        <div className="text-xs text-text-secondary flex items-center">
                          {skill.isActive ? (
                            <span className="flex items-center text-success">
                              <span className="w-1.5 h-1.5 bg-success rounded-full mr-1 animate-pulse"></span>
                              Active
                            </span>
                          ) : 'Inactive'}
                        </div>
                      </div>
                    </div>
                    <div className="flex flex-col items-end">
                      <span className="bg-primary/10 text-primary text-xs font-medium px-2 py-0.5 rounded-full mb-1">
                        Level {skill.level}
                      </span>
                      <span className="text-xs text-text-secondary">
                        {skill.currentXp.toLocaleString()} / {skill.xpForNextLevel.toLocaleString()} XP
                      </span>
                    </div>
                  </div>

                  <SkillProgressBar skill={skill} showActionProgress={true} />
                </div>
              </div>

              <div className="px-4 pb-4 flex justify-end mt-1">
                <button
                  className={`px-4 py-1.5 rounded text-sm font-medium transition-all flex items-center ${
                    skill.isActive 
                      ? 'bg-danger text-white hover:bg-danger/90 shadow-sm' 
                      : 'bg-primary text-white hover:bg-primary-dark shadow-sm'
                  }`}
                  onClick={(e) => handleToggleSkill(skill, e)}
                  aria-label={skill.isActive ? `Stop ${skill.name}` : `Start ${skill.name}`}
                >
                  {skill.isActive ? (
                    <>
                      <svg className="w-4 h-4 mr-1.5" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                        <rect x="6" y="5" width="4" height="14" rx="1" fill="currentColor" />
                        <rect x="14" y="5" width="4" height="14" rx="1" fill="currentColor" />
                      </svg>
                      Stop
                    </>
                  ) : (
                    <>
                      <svg className="w-4 h-4 mr-1.5" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                        <path d="M8 5.14V19.14L19 12.14L8 5.14Z" fill="currentColor" />
                      </svg>
                      Start
                    </>
                  )}
                </button>
              </div>

              {expandedSkillId === skill.id && (
                <div className="px-4 pb-4 pt-1 border-t border-panel-border bg-panel-bg/50">
                  <div className="grid grid-cols-2 gap-3 mb-3">
                    <div className="bg-panel-bg rounded-md p-3 flex flex-col">
                      <span className="text-xs text-text-secondary mb-1">XP per action</span>
                      <span className="font-medium text-text flex items-center">
                        <span className="text-primary mr-1">+</span>
                        {skill.xpPerAction}
                      </span>
                    </div>
                    <div className="bg-panel-bg rounded-md p-3 flex flex-col">
                      <span className="text-xs text-text-secondary mb-1">Action time</span>
                      <span className="font-medium text-text">
                        {skill.actionTimeInMs ? `${(skill.actionTimeInMs / 1000).toFixed(1)}s` : 'N/A'}
                      </span>
                    </div>
                  </div>

                  {skill.mastery && skill.mastery.unlocks && Object.keys(skill.mastery.unlocks).length > 0 && (
                    <div className="mt-4">
                      <h4 className="text-sm font-semibold mb-3 text-text flex items-center">
                        <span className="mr-1.5">🔓</span> Mastery Unlocks
                      </h4>
                      <div className="bg-panel-bg rounded-lg p-2">
                        <ul className="text-sm divide-y divide-panel-border">
                          {Object.entries(skill.mastery.unlocks).map(([level, description]) => (
                            <li 
                              key={level}
                              className={`py-2 px-2 ${
                                skill.level >= parseInt(level) 
                                  ? 'opacity-100' 
                                  : 'opacity-60'
                              }`}
                            >
                              <div className="flex items-start">
                                <span className={`inline-block shrink-0 rounded-full px-2 text-xs font-semibold ${
                                  skill.level >= parseInt(level) 
                                    ? 'bg-primary/10 text-primary' 
                                    : 'bg-gray-200 dark:bg-gray-700 text-text-secondary'
                                }`}>
                                  Lvl {level}
                                </span> 
                                <span className="ml-2 text-text">{description}</span>
                              </div>
                            </li>
                          ))}
                        </ul>
                      </div>
                    </div>
                  )}
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default SkillPanel;
