import { useState, useCallback } from 'react';
import { useGameContext } from '../context/GameContext';
import { GameState, Skill } from '../types';
import { calculateXpForLevel } from '../features/skills/SkillProgress';

/**
 * useSkills hook - Manages skill-related state and operations
 */
export const useSkills = () => {
  const { gameState, updateGameState } = useGameContext();
  const [currentSkillId, setCurrentSkillId] = useState<string | null>(null);
  
  const skills = gameState?.player.skills || {};
  const currentSkill = currentSkillId ? skills[currentSkillId] : null;
  
  /**
   * Select a skill to view its details
   */
  const selectSkill = useCallback((skillId: string) => {
    setCurrentSkillId(skillId);
  }, []);
  
  /**
   * Start a skill activity
   */
  const startSkill = useCallback((skillId: string) => {
    if (!gameState) return;
    
    const updatedSkills = {
      ...gameState.player.skills,
      [skillId]: {
        ...gameState.player.skills[skillId],
        isActive: true,
        lastActiveTime: Date.now()
      }
    };
    
    updateGameState({
      player: {
        ...gameState.player,
        skills: updatedSkills
      }
    });
  }, [gameState, updateGameState]);
  
  /**
   * Stop a skill activity
   */
  const stopSkill = useCallback((skillId: string) => {
    if (!gameState) return;
    
    const updatedSkills = {
      ...gameState.player.skills,
      [skillId]: {
        ...gameState.player.skills[skillId],
        isActive: false
      }
    };
    
    updateGameState({
      player: {
        ...gameState.player,
        skills: updatedSkills
      }
    });
  }, [gameState, updateGameState]);
  
  /**
   * Add experience to a skill and handle level ups
   */
  const addExperience = useCallback((skillId: string, amount: number) => {
    if (!gameState) return;
    
    const skill = gameState.player.skills[skillId];
    if (!skill) return;
    
    let newXp = skill.currentXp + amount;
    let newLevel = skill.level;
    let newRequiredXp = skill.requiredXp;
    
    // Check for level up
    while (newXp >= newRequiredXp) {
      newXp -= newRequiredXp;
      newLevel++;
      // Calculate new XP required for next level (example formula)
      newRequiredXp = Math.floor(newRequiredXp * 1.1);
    }
    
    const updatedSkills = {
      ...gameState.player.skills,
      [skillId]: {
        ...skill,
        level: newLevel,
        currentXp: newXp,
        requiredXp: newRequiredXp
      }
    };
    
    updateGameState({
      player: {
        ...gameState.player,
        skills: updatedSkills
      }
    });
  }, [gameState, updateGameState]);
  
  /**
   * Get a list of all active skills
   */
  const getActiveSkills = useCallback(() => {
    if (!gameState) return [];
    
    return Object.keys(gameState.player.skills).filter(
      skillId => gameState.player.skills[skillId].isActive
    );
  }, [gameState]);
  
  /**
   * Check if a skill has reached a specific level
   */
  const hasReachedLevel = useCallback((skillId: string, level: number) => {
    if (!gameState) return false;
    
    const skill = gameState.player.skills[skillId];
    return skill ? skill.level >= level : false;
  }, [gameState]);
  
  return {
    skills,
    currentSkill,
    selectSkill,
    startSkill,
    stopSkill,
    addExperience,
    getActiveSkills,
    hasReachedLevel
  };
};

/**
 * Calculate a new skill state after adding XP
 * Pure function for better testability
 */
export const calculateNewSkillState = (skill: Skill, xpAmount: number): Skill => {
  const newXp = skill.xp + xpAmount;
  let newLevel = skill.level;
  
  // Check for level ups
  while (newXp >= calculateXpForLevel(newLevel + 1)) {
    newLevel++;
  }
  
  // If mastery exists, update it as well
  let newMastery = skill.mastery;
  if (skill.mastery) {
    // Typically mastery gains a percentage of main XP
    const masteryXpGain = Math.floor(xpAmount * 0.1); 
    const newMasteryXp = skill.mastery.xp + masteryXpGain;
    let newMasteryLevel = skill.mastery.level;
    
    // Check for mastery level ups
    while (newMasteryXp >= calculateMasteryXpForLevel(newMasteryLevel + 1)) {
      newMasteryLevel++;
    }
    
    newMastery = {
      ...skill.mastery,
      level: newMasteryLevel,
      xp: newMasteryXp
    };
  }
  
  return {
    ...skill,
    level: newLevel,
    xp: newXp,
    mastery: newMastery
  };
};

/**
 * Calculate XP required for a mastery level
 */
export const calculateMasteryXpForLevel = (level: number): number => {
  return Math.floor(150 * Math.pow(level, 1.8));
};
