import React, { useState, useEffect } from 'react';
import { useCombat } from '../../hooks/useCombat';
import CombatLog from './CombatLog';

// Enemy type emojis mapping
const ENEMY_EMOJIS: Record<string, string> = {
  goblin: 'üë∫',
  rat: 'üêÄ',
  bandit: 'ü•∑',
  skeleton: 'üíÄ',
  slime: 'üü¢',
  spider: 'üï∑Ô∏è',
  wolf: 'üê∫',
  zombie: 'üßü',
  default: 'üëæ'
};

/**
 * CombatPanel component - Handles combat UI and interactions
 * Optimized for desktop viewing with reduced vertical space usage
 */
const CombatPanel: React.FC = () => {
  const {
    enemies,
    currentEnemy,
    isFighting,
    playerStats,
    combatLog,
    combatTick,
    startCombat,
    fleeCombat
  } = useCombat();

  // State for animations and effects
  const [playerAttackAnimation, setPlayerAttackAnimation] = useState(false);
  const [enemyAttackAnimation, setEnemyAttackAnimation] = useState(false);
  const [playerHitSplat, setPlayerHitSplat] = useState<number | null>(null);
  const [enemyHitSplat, setEnemyHitSplat] = useState<number | null>(null);
  const [showItemDrops, setShowItemDrops] = useState(false);

  // Trigger animations based on combat tick changes
  useEffect(() => {
    if (!isFighting) return;

    // Player attack animation
    setPlayerAttackAnimation(true);
    setTimeout(() => setPlayerAttackAnimation(false), 500);

    // Determine random hit value for display (visual only)
    const potentialPlayerDamage = Math.floor(Math.random() * playerStats.strength) + 1;
    setEnemyHitSplat(potentialPlayerDamage);
    setTimeout(() => setEnemyHitSplat(null), 1000);

    // Enemy attack animation with delay
    setTimeout(() => {
      setEnemyAttackAnimation(true);
      setTimeout(() => setEnemyAttackAnimation(false), 500);

      // Determine random hit value for display (visual only)
      if (currentEnemy) {
        const potentialEnemyDamage = Math.floor(Math.random() * currentEnemy.attack) + 1;
        setPlayerHitSplat(potentialEnemyDamage);
        setTimeout(() => setPlayerHitSplat(null), 1000);
      }
    }, 1000);

  }, [combatTick, isFighting, currentEnemy, playerStats.strength]);

  // Handle enemy selection
  const handleSelectEnemy = (enemyId: string) => {
    if (isFighting) return; // Prevent changing enemy during combat
    startCombat(enemyId);
  };

  // Handle fleeing from combat
  const handleFlee = () => {
    fleeCombat();
  };

  // Get enemy emoji based on enemy ID
  const getEnemyEmoji = (enemyId: string) => {
    for (const [key, emoji] of Object.entries(ENEMY_EMOJIS)) {
      if (enemyId.toLowerCase().includes(key)) {
        return emoji;
      }
    }
    return ENEMY_EMOJIS.default;
  };

  return (
    <div className="bg-panel rounded-lg shadow-md border border-panel-border overflow-hidden h-full flex flex-col">
      <div className="relative px-5 py-3 bg-header-bg text-header-text">
        <h2 className="mt-0 mb-0 text-lg font-bold flex items-center">
          <span className="mr-2">‚öîÔ∏è</span> Combat Arena
        </h2>
      </div>

      <div className="flex-1 grid grid-cols-12 gap-3 p-3 overflow-hidden">
        {/* Left section - Enemy selection & combat log */}
        <div className="col-span-4 flex flex-col h-full overflow-hidden">
          {/* Enemy selection - 40% height */}
          <div className="mb-3 pb-3 border-b border-panel-border">
            <h3 className="mt-0 mb-2 text-sm font-semibold text-text flex items-center">
              <span className="mr-2 text-xs">üéØ</span> Choose Enemy
            </h3>
            <div className="grid grid-cols-2 gap-2 max-h-[calc(100%-30px)] overflow-y-auto pr-1 custom-scrollbar">
              {enemies.map(enemy => (
                <div 
                  key={enemy.id}
                  className={`bg-item-bg border rounded-md p-2 cursor-pointer transition-all duration-200 text-sm
                    ${currentEnemy?.id === enemy.id 
                      ? 'border-primary shadow-sm' 
                      : 'border-item-border'}`}
                  onClick={() => handleSelectEnemy(enemy.id)}
                >
                  <div className="flex items-center gap-2">
                    <div className="text-2xl bg-panel-bg w-8 h-8 rounded-full flex items-center justify-center shadow-inner">
                      {getEnemyEmoji(enemy.id)}
                    </div>
                    <div className="flex flex-col">
                      <h4 className="font-bold text-xs m-0 text-text">{enemy.name}</h4>
                      <div className="text-xxs text-text-secondary">CP: {Math.floor((enemy.attack + enemy.defense) / 2)}</div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Combat log - 60% height */}
          <div className="flex-1 overflow-hidden">
            <h3 className="mt-0 mb-2 text-sm font-semibold text-text flex items-center">
              <span className="mr-2 text-xs">üìú</span> Combat Log
            </h3>
            <div className="h-[calc(100%-30px)]">
              <CombatLog logs={combatLog} />
            </div>
          </div>
        </div>

        {/* Right section - Combat details */}
        <div className="col-span-8 flex flex-col h-full overflow-hidden">
          {currentEnemy ? (
            <>
              {/* Combat status display */}
              <div className="bg-item-bg rounded-lg p-3 mb-3 relative">
                <div className="absolute right-2 top-2">
                  <span className="inline-block bg-primary/10 text-primary text-xs px-2 py-0.5 rounded-full">
                    Combat Power {Math.floor((currentEnemy.attack + currentEnemy.defense) / 2)}
                  </span>
                </div>

                <div className="text-lg font-bold text-text mb-2 flex items-center">
                  <span className="text-xl mr-2">{getEnemyEmoji(currentEnemy.id)}</span>
                  {currentEnemy.name}
                </div>

                <div className="grid grid-cols-2 gap-3 mb-3">
                  <div className="flex flex-col gap-1">
                    <div className="flex justify-between items-center">
                      <label className="text-xs font-medium text-text-secondary">Enemy:</label>
                      <span className="text-xs text-text">{currentEnemy.health} / {currentEnemy.maxHealth}</span>
                    </div>
                    <div className="h-2 bg-gray-200 dark:bg-gray-700 rounded-full overflow-hidden shadow-inner">
                      <div 
                        className="h-full bg-gradient-to-r from-red-500 to-red-600 transition-all duration-500"
                        style={{ width: `${(currentEnemy.health / currentEnemy.maxHealth) * 100}%` }}
                      ></div>
                    </div>
                  </div>

                  <div className="flex flex-col gap-1">
                    <div className="flex justify-between items-center">
                      <label className="text-xs font-medium text-text-secondary">You:</label>
                      <span className="text-xs text-text">{Math.floor(playerStats.health)} / {playerStats.maxHealth}</span>
                    </div>
                    <div className="h-2 bg-gray-200 dark:bg-gray-700 rounded-full overflow-hidden shadow-inner">
                      <div 
                        className="h-full bg-gradient-to-r from-green-500 to-green-600 transition-all duration-500"
                        style={{ width: `${(playerStats.health / playerStats.maxHealth) * 100}%` }}
                      ></div>
                    </div>
                  </div>
                </div>

                {/* Battle visual representation */}
                <div className="flex justify-between items-center py-3 px-2 bg-panel-bg rounded-lg mb-2 relative overflow-hidden">
                  <div className="absolute inset-0 bg-panel-bg opacity-50 z-0"></div>

                  <div className={`relative z-10 flex flex-col items-center ${playerAttackAnimation ? 'animate-player-attack' : ''}`}>
                    <div className="text-3xl mb-1">üßô</div>
                    <div className="text-xxs font-medium">You</div>
                    {playerHitSplat !== null && (
                      <div className="absolute -top-2 -right-2 bg-danger/90 text-white rounded-full w-6 h-6 flex items-center justify-center font-bold animate-hit-splat shadow-md border border-white text-xs">
                        {playerHitSplat}
                      </div>
                    )}
                  </div>

                  <div className="relative z-10 flex flex-col items-center">
                    <div className="text-sm font-bold text-text py-1 px-2 bg-panel border border-panel-border rounded-full">VS</div>
                    {isFighting && (
                      <div className="mt-1 px-2 py-0.5 bg-primary/10 text-primary text-xxs rounded-full">
                        Auto-fighting
                      </div>
                    )}
                  </div>

                  <div className={`relative z-10 flex flex-col items-center ${enemyAttackAnimation ? 'animate-enemy-attack' : ''}`}>
                    <div className="text-3xl mb-1">{getEnemyEmoji(currentEnemy.id)}</div>
                    <div className="text-xxs font-medium">{currentEnemy.name}</div>
                    {enemyHitSplat !== null && (
                      <div className="absolute -top-2 -left-2 bg-success/90 text-white rounded-full w-6 h-6 flex items-center justify-center font-bold animate-hit-splat shadow-md border border-white text-xs">
                        {enemyHitSplat}
                      </div>
                    )}
                  </div>
                </div>
              </div>

              {/* Combat stats and actions */}
              <div className="grid grid-cols-2 gap-3">
                <div className="bg-item-bg rounded-lg p-3">
                  <h4 className="mt-0 mb-2 text-sm font-semibold text-text flex items-center">
                    <span className="mr-2 text-xs">üìä</span> Combat Stats
                  </h4>
                  <div className="grid grid-cols-3 gap-2">
                    <div className="bg-panel-bg rounded-md p-1.5 flex flex-col items-center">
                      <span className="text-xxs text-text-secondary">Attack</span>
                      <span className="text-text font-semibold text-sm">{playerStats.attack}</span>
                    </div>
                    <div className="bg-panel-bg rounded-md p-1.5 flex flex-col items-center">
                      <span className="text-xxs text-text-secondary">Strength</span>
                      <span className="text-text font-semibold text-sm">{playerStats.strength}</span>
                    </div>
                    <div className="bg-panel-bg rounded-md p-1.5 flex flex-col items-center">
                      <span className="text-xxs text-text-secondary">Defense</span>
                      <span className="text-text font-semibold text-sm">{playerStats.defense}</span>
                    </div>
                  </div>
                </div>

                <div className="bg-item-bg rounded-lg p-3 flex flex-col">
                  <h4 className="mt-0 mb-2 text-sm font-semibold text-text flex items-center">
                    <span className="mr-2 text-xs">‚ö°</span> Actions
                  </h4>
                  <div className="flex-1 flex items-center justify-center">
                    <button 
                      className={`w-full py-1.5 px-3 rounded-md font-semibold transition-colors duration-200 flex items-center justify-center text-sm
                        ${!isFighting ? 'bg-gray-400 text-white cursor-not-allowed' : 'bg-danger text-white hover:bg-danger-dark'}`}
                      onClick={handleFlee}
                      disabled={!isFighting}
                    >
                      <span className="mr-2">üèÉ</span> Flee
                    </button>
                  </div>
                </div>
              </div>

              <div className="text-xs bg-panel-bg p-2 rounded-md text-text-secondary mt-3">
                <p className="flex items-center text-xxs"><span className="mr-1">‚ÑπÔ∏è</span> Combat is automatic. Your character attacks every 2 seconds.</p>
              </div>
            </>
          ) : (
            <div className="flex-1 flex flex-col items-center justify-center bg-item-bg rounded-lg text-text opacity-70">
              <div className="text-3xl mb-3">üéØ</div>
              <p className="text-center text-sm">
                Select an enemy from the list to begin combat.
                <br />
                <span className="text-xs text-text-secondary mt-2 block">Defeating enemies will reward you with XP and loot!</span>
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default CombatPanel;
