import React from 'react';
import { useSkills } from '../../hooks/useSkills';
import MasteryPanel from '../../components/MasteryPanel';
import { SkillProgress } from './SkillProgress';

interface SkillPanelProps {
  skillId: string;
}

/**
 * SkillPanel component - Displays a single skill with its actions
 */
const SkillPanel: React.FC<SkillPanelProps> = ({ skillId }) => {
  const { skills, startSkill, stopSkill } = useSkills();
  const skill = skills[skillId];

  if (!skill) {
    return <div className="skill-panel skill-not-found">Skill not found</div>;
  }

  const handleToggleSkill = () => {
    if (skill.isActive) {
      stopSkill(skillId);
    } else {
      startSkill(skillId);
    }
  };

  return (
    <div className={`skill-panel ${skill.isActive ? 'active' : ''}`}>
      <div className="skill-header">
        <h3 className="skill-name">{skill.name}</h3>
        <span className="skill-level">Level {skill.level}</span>
      </div>
      
      <SkillProgress skill={skill} />
      
      <SkillActions 
        isActive={skill.isActive} 
        onToggle={handleToggleSkill} 
      />
      
      {skill.mastery && (
        <div className="skill-mastery-section">
          <h4>Mastery</h4>
          <MasteryPanel mastery={skill.mastery} />
        </div>
      )}
    </div>
  );
};

/**
 * SkillActions component - Handles skill action buttons
 * Extracted for better separation of concerns
 */
interface SkillActionsProps {
  isActive: boolean;
  onToggle: () => void;
}

const SkillActions: React.FC<SkillActionsProps> = ({ isActive, onToggle }) => {
  return (
    <div className="skill-actions">
      <button 
        className={`skill-action-btn ${isActive ? 'active' : ''}`}
        onClick={onToggle}
      >
        {isActive ? 'Stop' : 'Start'}
      </button>
    </div>
  );
};

export default SkillPanel;
