import React from 'react';
import { useSkills } from '../../hooks/useSkills';
import { Skill } from '../../types';
import MasteryPanel from '../../components/MasteryPanel';

interface SkillPanelProps {
  skillId: string;
}

const SkillPanel: React.FC<SkillPanelProps> = ({ skillId }) => {
  const { skills, startSkill, stopSkill } = useSkills();
  const skill = skills[skillId];

  if (!skill) {
    return <div>Skill not found</div>;
  }

  const handleToggleSkill = () => {
    if (skill.isActive) {
      stopSkill(skillId);
    } else {
      startSkill(skillId);
    }
  };

  return (
    <div className="skill-panel">
      <h3>{skill.name}</h3>
      <div className="skill-info">
        <p>Level: {skill.level}</p>
        <p>XP: {skill.xp}</p>
        <div className="progress-bar">
          <div 
            className="progress" 
            style={{ width: `${calculateProgressPercentage(skill)}%` }}
          ></div>
        </div>
      </div>
      <button 
        className={`skill-action-btn ${skill.isActive ? 'active' : ''}`}
        onClick={handleToggleSkill}
      >
        {skill.isActive ? 'Stop' : 'Start'}
      </button>
      {skill.mastery && <MasteryPanel mastery={skill.mastery} />}
    </div>
  );
};

const calculateProgressPercentage = (skill: Skill): number => {
  const currentLevelXp = calculateXpForLevel(skill.level);
  const nextLevelXp = calculateXpForLevel(skill.level + 1);
  const xpInCurrentLevel = skill.xp - currentLevelXp;
  const xpRequiredForNextLevel = nextLevelXp - currentLevelXp;
  
  return Math.min(100, Math.floor((xpInCurrentLevel / xpRequiredForNextLevel) * 100));
};

const calculateXpForLevel = (level: number): number => {
  return Math.floor(100 * (Math.pow(level, 1.5)));
};

export default SkillPanel;
