import { useGameContext } from '../context/GameContext';
import { ENEMIES } from '../constants';
import { GameState } from '../types';

export const useCombat = () => {
  const { gameState, updateGameState } = useGameContext();
  
  const startCombat = (enemyId: string) => {
    if (!gameState) return;
    
    const enemy = ENEMIES[enemyId];
    if (!enemy) return;
    
    updateGameState((prevState: GameState | null) => {
      if (!prevState) return prevState;
      
      return {
        ...prevState,
        player: {
          ...prevState.player,
          combat: {
            currentEnemy: { ...enemy },
            isFighting: true
          }
        }
      };
    });
  };

  const stopCombat = () => {
    if (!gameState) return;
    
    updateGameState((prevState: GameState | null) => {
      if (!prevState) return prevState;
      
      return {
        ...prevState,
        player: {
          ...prevState.player,
          combat: {
            ...prevState.player.combat,
            isFighting: false
          }
        }
      };
    });
  };
  
  return {
    combat: gameState?.player.combat,
    startCombat,
    stopCombat
  };
};
