import React from 'react';

interface Mastery {
  level: number;
  xp: number;
  unlocks: { [level: string]: string };
}

interface MasteryPanelProps {
  mastery: Mastery;
}

const MasteryPanel: React.FC<MasteryPanelProps> = ({ mastery }) => {
  return (
    <div className="mastery-panel">
      <h4>Mastery Level: {mastery.level}</h4>
      <div className="mastery-progress">
        <div className="progress-bar">
          <div 
            className="progress" 
            style={{ width: `${calculateMasteryProgress(mastery)}%` }}
          ></div>
        </div>
      </div>

      {Object.keys(mastery.unlocks).length > 0 && (
        <div className="mastery-unlocks">
          <h5>Unlocks:</h5>
          <ul>
            {Object.entries(mastery.unlocks).map(([level, description]) => (
              <li key={level} className={parseInt(level) <= mastery.level ? 'unlocked' : 'locked'}>
                Level {level}: {description}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

const calculateMasteryProgress = (mastery: Mastery): number => {
  const currentLevelXp = calculateMasteryXpForLevel(mastery.level);
  const nextLevelXp = calculateMasteryXpForLevel(mastery.level + 1);
  const xpInCurrentLevel = mastery.xp - currentLevelXp;
  const xpRequiredForNextLevel = nextLevelXp - currentLevelXp;

  return Math.min(100, Math.floor((xpInCurrentLevel / xpRequiredForNextLevel) * 100));
};

const calculateMasteryXpForLevel = (level: number): number => {
  return Math.floor(150 * (Math.pow(level, 1.8)));
};

export default MasteryPanel;
