import React, { useRef, useEffect } from 'react';

interface CombatLogProps {
  logs: string[];
}

/**
 * CombatLog component - Displays a scrollable log of combat events
 */
const CombatLog: React.FC<CombatLogProps> = ({ logs }) => {
  const logContainerRef = useRef<HTMLDivElement>(null);

  // Auto-scroll to the bottom when new logs are added
  useEffect(() => {
    if (logContainerRef.current) {
      logContainerRef.current.scrollTop = logContainerRef.current.scrollHeight;
    }
  }, [logs]);

  // Helper function to determine message type for styling
  const getMessageType = (message: string): string => {
    if (message.includes('hit') && message.includes('damage')) {
      return message.includes('You hit') 
        ? 'border-l-success bg-success/10' 
        : 'border-l-danger bg-danger/10';
    } else if (message.includes('missed')) {
      return 'border-l-gray-400 bg-gray-400/10 opacity-80';
    } else if (message.includes('defeated')) {
      return 'border-l-warning bg-warning/10 font-bold';
    } else if (message.includes('Congratulations')) {
      return 'border-l-purple-600 bg-purple-600/10 font-bold';
    } else if (message.includes('found') || message.includes('received')) {
      return 'border-l-primary bg-primary/10';
    } else if (message.includes('fled') || message.includes('flee')) {
      return message.includes('successfully') 
        ? 'border-l-warning bg-warning/10' 
        : 'border-l-danger bg-danger/10';
    } else if (message.includes('defeated')) {
      return 'border-l-purple-600 bg-purple-600/10 font-bold';
    }
    return 'border-l-primary bg-primary/10';
  };

  return (
    <div className="max-h-[300px] flex flex-col">
      <div 
        className="flex-1 overflow-y-auto border border-panel-border rounded-md bg-item-bg p-2.5 max-h-[300px]" 
        ref={logContainerRef}
      >
        {logs.length === 0 ? (
          <div className="p-5 text-center italic text-text opacity-70">No combat activity yet</div>
        ) : (
          logs.map((log, index) => (
            <div 
              key={index} 
              className={`py-1.5 px-2 mb-1 rounded text-sm border-l-3 transition-colors duration-300 hover:bg-item-bg text-text ${getMessageType(log)}`}
            >
              {log}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default CombatLog;
