import React, { useState, useEffect } from 'react';
import { useCombat } from '../../hooks/useCombat';
import CombatLog from './CombatLog';

// Enemy type emojis mapping
const ENEMY_EMOJIS: Record<string, string> = {
  goblin: '👺',
  rat: '🐀',
  bandit: '🥷',
  skeleton: '💀',
  slime: '🟢',
  spider: '🕷️',
  wolf: '🐺',
  zombie: '🧟',
  default: '👾'
};

/**
 * CombatPanel component - Handles combat UI and interactions
 */
const CombatPanel: React.FC = () => {
  const {
    enemies,
    currentEnemy,
    isFighting,
    playerStats,
    combatLog,
    combatTick,
    startCombat,
    fleeCombat
  } = useCombat();

  // State for animations and effects
  const [playerAttackAnimation, setPlayerAttackAnimation] = useState(false);
  const [enemyAttackAnimation, setEnemyAttackAnimation] = useState(false);
  const [playerHitSplat, setPlayerHitSplat] = useState<number | null>(null);
  const [enemyHitSplat, setEnemyHitSplat] = useState<number | null>(null);
  const [showItemDrops, setShowItemDrops] = useState(false);

  // Trigger animations based on combat tick changes
  useEffect(() => {
    if (!isFighting) return;

    // Player attack animation
    setPlayerAttackAnimation(true);
    setTimeout(() => setPlayerAttackAnimation(false), 500);

    // Determine random hit value for display (visual only)
    const potentialPlayerDamage = Math.floor(Math.random() * playerStats.strength) + 1;
    setEnemyHitSplat(potentialPlayerDamage);
    setTimeout(() => setEnemyHitSplat(null), 1000);

    // Enemy attack animation with delay
    setTimeout(() => {
      setEnemyAttackAnimation(true);
      setTimeout(() => setEnemyAttackAnimation(false), 500);

      // Determine random hit value for display (visual only)
      if (currentEnemy) {
        const potentialEnemyDamage = Math.floor(Math.random() * currentEnemy.attack) + 1;
        setPlayerHitSplat(potentialEnemyDamage);
        setTimeout(() => setPlayerHitSplat(null), 1000);
      }
    }, 1000);

  }, [combatTick, isFighting, currentEnemy, playerStats.strength]);

  // Handle enemy selection
  const handleSelectEnemy = (enemyId: string) => {
    if (isFighting) return; // Prevent changing enemy during combat
    startCombat(enemyId);
  };

  // Handle fleeing from combat
  const handleFlee = () => {
    fleeCombat();
  };

  // Get enemy emoji based on enemy ID
  const getEnemyEmoji = (enemyId: string) => {
    for (const [key, emoji] of Object.entries(ENEMY_EMOJIS)) {
      if (enemyId.toLowerCase().includes(key)) {
        return emoji;
      }
    }
    return ENEMY_EMOJIS.default;
  };

  return (
    <div className="bg-panel rounded-lg shadow-md border border-panel-border overflow-hidden">
      <div className="relative px-5 py-4 bg-header-bg text-header-text">
        <h2 className="mt-0 mb-0 text-xl font-bold flex items-center">
          <span className="mr-2">⚔️</span> Combat Arena
        </h2>
      </div>

      <div className="p-5 grid grid-cols-1 md:grid-cols-3 gap-5">
        <div className="md:col-span-1 md:border-r border-panel-border md:pr-5">
          <h3 className="mt-0 mb-4 text-lg font-semibold text-text flex items-center">
            <span className="mr-2 text-sm">🎯</span> Choose an Enemy
          </h3>
          <div className="flex flex-col gap-3 max-h-[400px] overflow-y-auto pr-2 custom-scrollbar">
            {enemies.map(enemy => (
              <div 
                key={enemy.id}
                className={`bg-item-bg border-2 rounded-md p-3.5 cursor-pointer transition-all duration-200 hover:translate-y-[-3px] hover:shadow-md
                  ${currentEnemy?.id === enemy.id 
                    ? 'border-primary shadow-md' 
                    : 'border-item-border'}`}
                onClick={() => handleSelectEnemy(enemy.id)}
              >
                <div className="flex items-center gap-3">
                  <div className="text-3xl bg-panel-bg w-12 h-12 rounded-full flex items-center justify-center shadow-inner">
                    {getEnemyEmoji(enemy.id)}
                  </div>
                  <div className="flex flex-col flex-1">
                    <h4 className="font-bold text-base m-0 text-text">{enemy.name}</h4>
                    <div className="mt-1 flex gap-2 text-xs">
                      <span className="bg-panel-bg px-2 py-0.5 rounded text-text font-medium">Combat Lvl {enemy.level}</span>
                    </div>
                  </div>
                </div>
                <div className="grid grid-cols-3 gap-2 mt-3 text-xs">
                  <div className="flex flex-col items-center bg-panel-bg rounded p-1.5">
                    <span className="text-text-secondary">ATK</span>
                    <span className="font-semibold text-text">{enemy.attack}</span>
                  </div>
                  <div className="flex flex-col items-center bg-panel-bg rounded p-1.5">
                    <span className="text-text-secondary">DEF</span>
                    <span className="font-semibold text-text">{enemy.defense}</span>
                  </div>
                  <div className="flex flex-col items-center bg-panel-bg rounded p-1.5">
                    <span className="text-text-secondary">HP</span>
                    <span className="font-semibold text-text">{enemy.maxHealth}</span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        <div className="md:col-span-2">
          <h3 className="mt-0 mb-4 text-lg font-semibold text-text flex items-center">
            <span className="mr-2 text-sm">🔥</span> Combat Status
          </h3>
          {currentEnemy ? (
            <div className="flex flex-col gap-4">
              <div className="flex flex-col">
                <div className="bg-item-bg rounded-lg p-5 relative">
                  <div className="absolute right-3 top-3">
                    <span className="inline-block bg-primary/10 text-primary text-xs px-2 py-0.5 rounded-full">
                      Level {currentEnemy.level}
                    </span>
                  </div>

                  <div className="text-xl font-bold text-text mb-3 flex items-center">
                    <span className="text-2xl mr-3">{getEnemyEmoji(currentEnemy.id)}</span>
                    {currentEnemy.name}
                  </div>

                  <div className="flex flex-col gap-3 mb-5">
                    <div className="flex flex-col gap-1.5">
                      <div className="flex justify-between items-center">
                        <label className="text-sm font-medium text-text-secondary">Enemy Health:</label>
                        <span className="text-sm text-text">{currentEnemy.health} / {currentEnemy.maxHealth}</span>
                      </div>
                      <div className="h-3 bg-gray-200 dark:bg-gray-700 rounded-full overflow-hidden shadow-inner">
                        <div 
                          className="h-full bg-gradient-to-r from-red-500 to-red-600 transition-all duration-500"
                          style={{ width: `${(currentEnemy.health / currentEnemy.maxHealth) * 100}%` }}
                        ></div>
                      </div>
                    </div>

                    <div className="flex flex-col gap-1.5">
                      <div className="flex justify-between items-center">
                        <label className="text-sm font-medium text-text-secondary">Your Health:</label>
                        <span className="text-sm text-text">{Math.floor(playerStats.health)} / {playerStats.maxHealth}</span>
                      </div>
                      <div className="h-3 bg-gray-200 dark:bg-gray-700 rounded-full overflow-hidden shadow-inner">
                        <div 
                          className="h-full bg-gradient-to-r from-green-500 to-green-600 transition-all duration-500"
                          style={{ width: `${(playerStats.health / playerStats.maxHealth) * 100}%` }}
                        ></div>
                      </div>
                    </div>
                  </div>

                  <div className="flex justify-between items-center py-6 px-2 bg-panel-bg rounded-lg mb-4 relative overflow-hidden">
                    <div className="absolute inset-0 bg-panel-bg opacity-50 z-0"></div>

                    <div className={`relative z-10 flex flex-col items-center ${playerAttackAnimation ? 'animate-player-attack' : ''}`}>
                      <div className="text-5xl mb-2">🧙</div>
                      <div className="text-xs font-medium">You</div>
                      {playerHitSplat !== null && (
                        <div className="absolute -top-4 -right-4 bg-danger/90 text-white rounded-full w-8 h-8 flex items-center justify-center font-bold animate-hit-splat shadow-md border border-white">
                          {playerHitSplat}
                        </div>
                      )}
                    </div>

                    <div className="relative z-10 flex flex-col items-center">
                      <div className="text-xl font-bold text-text py-1 px-3 bg-panel border border-panel-border rounded-full">VS</div>
                      {isFighting && (
                        <div className="mt-2 px-2 py-0.5 bg-primary/10 text-primary text-xs rounded-full">
                          Auto-fighting
                        </div>
                      )}
                    </div>

                    <div className={`relative z-10 flex flex-col items-center ${enemyAttackAnimation ? 'animate-enemy-attack' : ''}`}>
                      <div className="text-5xl mb-2">{getEnemyEmoji(currentEnemy.id)}</div>
                      <div className="text-xs font-medium">{currentEnemy.name}</div>
                      {enemyHitSplat !== null && (
                        <div className="absolute -top-4 -left-4 bg-success/90 text-white rounded-full w-8 h-8 flex items-center justify-center font-bold animate-hit-splat shadow-md border border-white">
                          {enemyHitSplat}
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="bg-item-bg rounded-lg p-4">
                  <h4 className="mt-0 mb-3 text-base font-semibold text-text flex items-center">
                    <span className="mr-2 text-sm">📊</span> Your Combat Stats
                  </h4>
                  <div className="grid grid-cols-3 gap-3">
                    <div className="bg-panel-bg rounded-md p-2 flex flex-col items-center">
                      <span className="text-xs text-text-secondary">Attack</span>
                      <span className="text-text font-semibold">{playerStats.attack}</span>
                    </div>
                    <div className="bg-panel-bg rounded-md p-2 flex flex-col items-center">
                      <span className="text-xs text-text-secondary">Strength</span>
                      <span className="text-text font-semibold">{playerStats.strength}</span>
                    </div>
                    <div className="bg-panel-bg rounded-md p-2 flex flex-col items-center">
                      <span className="text-xs text-text-secondary">Defense</span>
                      <span className="text-text font-semibold">{playerStats.defense}</span>
                    </div>
                  </div>
                </div>

                <div className="bg-item-bg rounded-lg p-4 flex flex-col">
                  <h4 className="mt-0 mb-3 text-base font-semibold text-text flex items-center">
                    <span className="mr-2 text-sm">⚡</span> Combat Actions
                  </h4>
                  <div className="flex-1 flex items-center justify-center">
                    <button 
                      className={`w-full py-2 px-4 rounded-md font-semibold transition-colors duration-200 flex items-center justify-center
                        ${!isFighting ? 'bg-gray-400 text-white cursor-not-allowed' : 'bg-danger text-white hover:bg-danger-dark'}`}
                      onClick={handleFlee}
                      disabled={!isFighting}
                    >
                      <span className="mr-2">🏃</span> Flee from Combat
                    </button>
                  </div>
                </div>
              </div>

              <div className="text-sm bg-panel-bg p-3 rounded-md text-text-secondary">
                <p className="mb-1 flex items-center"><span className="mr-2">ℹ️</span> Combat is automatic. Your character will attack every 2 seconds.</p>
                <p className="mb-0">Successfully landing hits will earn you Combat XP and potentially rewards.</p>
              </div>
            </div>
          ) : (
            <div className="flex flex-col items-center justify-center h-[300px] bg-item-bg rounded-lg text-text opacity-70">
              <div className="text-4xl mb-4">🎯</div>
              <p className="text-center max-w-xs">
                Select an enemy from the list to begin combat.
                <br /><br />
                <span className="text-sm">Defeating enemies will reward you with XP and loot!</span>
              </p>
            </div>
          )}

          <div className="mt-5 pt-5 border-t border-panel-border">
            <h3 className="mt-0 mb-4 text-lg font-semibold text-text flex items-center">
              <span className="mr-2 text-sm">📜</span> Combat Log
            </h3>
            <CombatLog logs={combatLog} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default CombatPanel;
